mapper.update:
UPDATE t_log
SET status=#{status}
WHERE id = #{id}

mapper.select:
SELECT id, lid, uid,guest_name AS username, guest_email AS email, `date`, `desc`
FROM t_reply
WHERE lid = #{id}

SELECT id, lid, uid,guest_name AS username, guest_email AS email, `date`, `desc`
FROM t_reply
WHERE id = #{id} LIMIT 1;

SELECT id, lid, htmllog AS log, res_url
FROM t_log_detail
WHERE lid = #{id} LIMIT 1;

mapper.update:
UPDATE t_log
title = #{title}, date = #{date}, gid = #{gid}, type = #{category.cid}, status = #{status}
WHERE id = #{id};

UPDATE t_log_detail
htmllog = #{log}, res_url = #{res_url},
WHERE id=#{id}

mapper.select:
SELECT SQL_CALC_FOUND_ROWS t_log.*


    , t_tag_log_map.tid AS "tid"


    , "0" AS "tid"


FROM t_log

    JOIN t_tag_log_map ON t_log.id = t_tag_log_map.lid



        (gid = #{gid}
        (gid = 0


        OR uid = #{uid})
        )

    AND t_tag_log_map.tid = #{tid}
    AND t_log.type = #{type}
    AND t_log.title LIKE #{title}
    AND status = #{status}

ORDER BY id DESC LIMIT #{offset},#{count}

SELECT *
FROM t_log
WHERE id = #{id} LIMIT 1;

mapper.delete:
DELETE
FROM t_category
WHERE id = #{cid}
  AND `desc` = #{desc};

DELETE
FROM t_account_category_map
WHERE id = #{id}
  AND uid = #{uid};

mapper.select:
SELECT id AS cid,`desc`
from t_category


        id = #{id}
        `desc` = #{desc}


LIMIT 1;

SELECT id, uid, type_id AS cid
FROM t_account_category_map
WHERE type_id = #{cid}
  AND uid = #{uid} LIMIT 1;

SELECT t_tag.id, t_tag.desc, t_tag_log_map.id AS mid
FROM t_tag
         JOIN t_tag_log_map
WHERE t_tag_log_map.lid = #{id}
  AND t_tag_log_map.tid = t_tag.id;

SELECT t_tag.id, t_tag.desc FROM t_tag JOIN t_tag_log_map JOIN t_log ON
(t_log.uid = #{uid} OR t_log.gid = 0) t_log.gid = 0
AND t_tag_log_map.tid = t_tag.id AND t_tag_log_map.lid = t_log.id GROUP BY t_tag.id;

mapper.delete:
